import streamlit as st
import numpy as np
import pandas as pd
import matplotlib.pyplot as plt
import plotly.express as px

st.title("Hello Streamlit")
st.write(":100: This is your first Streamlit App") #emojis can only be added in write func
st.text("Lets get started")

#conditional
name = st.text_input("Enter your name:") #We are accepting the input from the user
if st.button("Greet"): #st.button will create a button called Greet
    st.success(f":smile: Hello {name}. I hope you are doing fine.")

#Checking if number is positive or negative
num = st.number_input("Enter the number:")
if st.button("Postive/Negative"):
    if num>0:
        st.success(f"{num} is positive")
    elif num<0:
        st.success(f"{num} is negative")
    else:
        st.success("Number entered is 0")

#Displaying the charts in streamlit
df = pd.DataFrame(np.random.randn(10, 2), columns=['A','B'])
st.line_chart(df)

st.bar_chart(df)

st.scatter_chart(df)

st.table(df)

#Media Layout and advance widget
st.sidebar.title("Navigation")
st.image("data:image/jpeg;base64,", caption="Sample Image",width=750)
st.video("https://youtu.be/ERClHCOF14c?si=M2fJiRJ_HL-wvqGt")

#Uploading file
st.sidebar.button("Upload CSV File")
st.sidebar.file_uploader("Upload CSV file") #To upload files in sidebar
upload_file = st.file_uploader("Upload a csv file", type="csv")
if upload_file:
    df = pd.read_csv(upload_file)
    st.dataframe(df)

st.title("Text and Markdown Demo")
st.header("This is a header")
st.subheader("This is a subheader")
st.markdown("**Bold**, *Italic*, `Code`, [Link](https://streamlit.io)")
st.code("for i in range(5): print(i)", language="python")


st.text_input("What's your name?")
st.text_area("How was your day?..")
st.number_input("Pick a num:", min_value=0, max_value=100)
st.slider("Choose a range", 0, 100)
st.selectbox("Select a fruit", ["Apple","Banana","Cherry","Guava","Pomogrante","Watermelon"])
st.multiselect("Choose toppings for your pizza :pizza: : ",["Chesse", "Olives","Corn","Pepporini","Tomatoes"])
st.radio("Pick one:",["Option A", "Option B"])
st.checkbox("I agree to the terms and conditions")
st.button("Submit")

if st.button("Click Me"):
    st.success("Button Clicked")

if st.checkbox("Show Details"):
    st.info("Here are more details")

option = st.radio("Pick one:",["Show Chart", "Show Table"])
if option == "Show Chart":
    st.write(":chart: Chart would appear here")
else:
    st.write(":skin-tone-4: Table would appear here")


#Using a Form for creating for login window 

with st.form("Login_Form"):
    username = st.text_input("Username")
    password = st.text_input("Password", type='password')
    submitted = st.form_submit_button("Login")

if submitted:
    st.success(f"Welcome, {username}!")

#Creating 2 cols: side by side

col1, col2 = st.columns(2)

with col1:
    st.button("Press me in column1")

with col2:
    st.button("Press me in column2")

